INSERT INTO technical_questions (question_number, question_text, topic, question_type, difficulty, option_a, option_b, option_c, option_d, correct_answer, explanation, tags) VALUES
(1, 'What does ACID stand for in databases?', 'backend', 'mcq', 'easy', 'Atomicity, Consistency, Isolation, Durability', 'Accuracy, Consistency, Isolation, Durability', 'Atomicity, Correctness, Integrity, Durability', 'Atomicity, Consistency, Integration, Durability', 0, 'ACID: Atomicity, Consistency, Isolation, Durability ensure reliable transactions.', ARRAY['db','transactions']),
(2, 'Which HTTP method is idempotent?', 'backend', 'mcq', 'easy', 'POST', 'PATCH', 'PUT', 'CONNECT', 2, 'PUT is idempotent; multiple identical requests result in the same state.', ARRAY['http','rest']),
(3, 'What status code indicates "Created"?', 'backend', 'mcq', 'easy', '200', '201', '202', '204', 1, '201 Created.', ARRAY['http','rest']),
(4, 'What is the primary purpose of a database index?', 'backend', 'mcq', 'easy', 'To increase storage size', 'To speed up data retrieval', 'To encrypt data', 'To backup data', 1, 'Indexes improve query speed by organizing data.', ARRAY['db','index']),
(5, 'What does REST stand for?', 'backend', 'mcq', 'easy', 'Remote End Service Transfer', 'Representational State Transfer', 'Relational Service Technology', 'Remote Extension Standard Technology', 1, 'REST is an architectural style for distributed systems.', ARRAY['http','api']),
(6, 'Which data structure is LIFO?', 'backend', 'mcq', 'easy', 'Queue', 'Stack', 'Tree', 'Graph', 1, 'Stack follows Last In, First Out principle.', ARRAY['dsa','structures']),
(7, 'What does a primary key ensure?', 'backend', 'mcq', 'easy', 'Automatic backup', 'Unique identification of rows', 'Encryption of data', 'Compression of storage', 1, 'Primary key uniquely identifies each row.', ARRAY['db','constraints']),
(8, 'Which protocol is used for secure HTTP?', 'backend', 'mcq', 'easy', 'HTTP/2', 'HTTPS', 'FTP', 'SMTP', 1, 'HTTPS uses SSL/TLS for encryption.', ARRAY['security','http']),
(9, 'What is a foreign key?', 'backend', 'mcq', 'easy', 'A key that opens databases', 'A reference to a primary key in another table', 'A backup key', 'An encryption key', 1, 'Foreign keys maintain referential integrity.', ARRAY['db','relationships']),
(10, 'What does JSON stand for?', 'backend', 'mcq', 'easy', 'Java System Object Notation', 'JavaScript Object Notation', 'Joint Standard Output Network', 'Java Serialized Object Network', 1, 'JSON is a lightweight data interchange format.', ARRAY['data','formats']),
(11, 'What is the base 2 number system called?', 'backend', 'mcq', 'easy', 'Decimal', 'Binary', 'Octal', 'Hexadecimal', 1, 'Binary is the base 2 number system.', ARRAY['basics','numbers']),
(12, 'Which sorting algorithm is most efficient in worst case?', 'backend', 'mcq', 'easy', 'Bubble Sort', 'Merge Sort', 'Selection Sort', 'Insertion Sort', 1, 'Merge Sort guarantees O(n log n) worst case.', ARRAY['dsa','sorting']),
(13, 'What is caching?', 'backend', 'mcq', 'easy', 'Deleting old data', 'Storing frequently accessed data in fast memory', 'Compressing files', 'Encrypting data', 1, 'Caching improves performance by reducing access time.', ARRAY['performance','memory']),
(14, 'What is API?', 'backend', 'mcq', 'easy', 'A programming language', 'Application Programming Interface for software communication', 'A database backup tool', 'A security protocol', 1, 'APIs enable communication between applications.', ARRAY['api','basics']),
(15, 'What does TCP stand for?', 'backend', 'mcq', 'easy', 'Transfer Control Protocol', 'Transmission Control Protocol', 'Transaction Control Protocol', 'Transport Communication Protocol', 1, 'TCP ensures reliable data transmission.', ARRAY['network','protocols']);
INSERT INTO technical_questions (question_number, question_text, topic, question_type, difficulty, option_a, option_b, option_c, option_d, correct_answer, explanation, tags) VALUES
(16, 'What is the time complexity of binary search?', 'backend', 'mcq', 'medium', 'O(n)', 'O(log n)', 'O(n log n)', 'O(1)', 1, 'Binary search halves the search space each step.', ARRAY['dsa','search']),
(17, 'Which index type is best for range queries?', 'backend', 'mcq', 'medium', 'Hash index', 'B-Tree index', 'Bitmap index', 'Full-text index', 1, 'B-Tree indexes support ordered scans and ranges.', ARRAY['db','index']),
(18, 'In SQL, what does `LEFT JOIN` return?', 'backend', 'mcq', 'medium', 'Only matching rows', 'All rows from left table and matching rows from right', 'All rows from right table and matching from left', 'Cartesian product', 1, 'LEFT JOIN preserves all rows from the left side.', ARRAY['sql','joins']),
(19, 'What is a primary benefit of using a message queue?', 'backend', 'mcq', 'medium', 'Lower latency than function calls', 'Tight coupling of services', 'Asynchronous decoupling and buffering', 'Guaranteed exactly-once delivery always', 2, 'Queues decouple producers/consumers and buffer load.', ARRAY['architecture','queues']),
(20, 'What is normalization in databases?', 'backend', 'mcq', 'medium', 'Compressing data', 'Organizing tables to reduce redundancy', 'Encrypting sensitive data', 'Backing up data', 1, 'Normalization minimizes data duplication.', ARRAY['db','design']),
(21, 'What does a transaction do?', 'backend', 'mcq', 'medium', 'Performs a single SQL statement', 'Bundles multiple operations into one atomic unit', 'Deletes old records', 'Creates backups', 1, 'Transactions ensure data consistency.', ARRAY['db','transactions']),
(22, 'What is the time complexity of quick sort average case?', 'backend', 'mcq', 'medium', 'O(n)', 'O(n^2)', 'O(n log n)', 'O(log n)', 2, 'Average case quicksort is O(n log n).', ARRAY['dsa','sorting']),
(23, 'What does NoSQL database typically store?', 'backend', 'mcq', 'medium', 'Only text', 'Semi-structured or unstructured data (JSON, documents)', 'Binary files only', 'Images only', 1, 'NoSQL stores flexible, non-relational data.', ARRAY['db','nosql']),
(24, 'What is eventual consistency?', 'backend', 'mcq', 'medium', 'Immediate data consistency everywhere', 'Data becomes consistent after some time', 'Data is never consistent', 'Consistency that requires passwords', 1, 'Eventual consistency is used in distributed systems.', ARRAY['distributed','consistency']),
(25, 'What is load balancing?', 'backend', 'mcq', 'medium', 'Balancing database weight', 'Distributing incoming requests across multiple servers', 'Storing balanced data', 'Encrypting server load', 1, 'Load balancing improves availability and performance.', ARRAY['infrastructure','scaling']),
(26, 'What is connection pooling?', 'backend', 'mcq', 'medium', 'Swimming pool of servers', 'Reusing database connections to reduce overhead', 'Pooling user connections', 'Backup connections', 1, 'Connection pooling improves efficiency.', ARRAY['db','performance']),
(27, 'What does horizontal scaling mean?', 'backend', 'mcq', 'medium', 'Making servers faster', 'Adding more servers to distribute load', 'Making servers wider', 'Removing servers', 1, 'Horizontal scaling adds more machines.', ARRAY['infrastructure','scaling']),
(28, 'What is rate limiting?', 'backend', 'mcq', 'medium', 'Limiting content rating', 'Controlling how many requests a client can make', 'Limiting server speed', 'Limiting database size', 1, 'Rate limiting prevents abuse and overload.', ARRAY['security','api']),
(29, 'What is stateless architecture?', 'backend', 'mcq', 'medium', 'Servers that remember nothing', 'Each request contains all needed information', 'Servers that crash often', 'Servers with no state file', 1, 'Stateless services are easier to scale.', ARRAY['architecture','design']),
(30, 'What does Swagger/OpenAPI provide?', 'backend', 'mcq', 'medium', 'Weather information', 'API documentation and specification', 'Backup solutions', 'Security encryption', 1, 'Swagger/OpenAPI standardizes API documentation.', ARRAY['api','documentation']);
INSERT INTO technical_questions (question_number, question_text, topic, question_type, difficulty, option_a, option_b, option_c, option_d, correct_answer, explanation, tags) VALUES
